package pct;

public class Exercicio07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mediaA = (8 + 9 + 7) / 3;
		int mediaB = (4 + 5 + 6) / 3;
		
		int soma = mediaA + mediaB;
		int media = soma / 2;
		
		System.out.println("8, 9 e 7: " + mediaA);
		System.out.println("4, 5 e 6: " + mediaB);
		System.out.println("Soma das medias: " + soma);
		System.out.println("Media das medias: " + media);
	}

}
